function addPromise(a,b){
    return new Promise(function(resolve,reject){
        if(typeof a === ‘number’ &amp;&amp; typeof b === ‘number’){
            resolve(a+b)
        } else {
            reject(“these are not two numbers”)
        }
    })
}
